import axios from "axios";

async function updateSkateStatus(userId, obj) {
  try {
    let response = await axios.put(
      "http://localhost:3007/api/users/skateStatus/" + userId,
      obj
    );
    if (response) {
      return response.data;
    }
  } catch (error) {
    console.log("Error getting status: " + error);
  }
}
async function addToCurrentPark(userId, obj) {
  try {
    let response = await axios.put(
      "http://localhost:3007/api/users/addToPendingFriends/" + userId,
      obj
    );
    if (response) {
      return response.data;
    }
  } catch (error) {
    console.log("Error adding friend: " + error);
  }
}
async function removeFromCurrentPark(userId, obj) {
  try {
    let response = await axios.put(
      "http://localhost:3007/api/users/removeFromFriendRequests/" + userId,
      obj
    );
    if (response) {
      return response.data;
    }
  } catch (error) {
    console.log("Error removing friend: " + error);
  }
}

const AxiosSkateStatus = {
  updateSkateStatus,
  addToCurrentPark,
  removeFromCurrentPark
};
export default AxiosSkateStatus;
