import React from "react";
import CustomButton from "./likeButton";
import {AxiosUsers} from "../../Routes/userRoutes";

const DisplayPosts = ({ postList, setHidden, setSinglePost }) => {
  function handleClick() {
    setHidden(true);
  }
  async function findName(userId){
    let name = await AxiosUsers.getUserName(userId);
    return name ;
  }


  return (
    <div>
      {postList.map((post, index) => {
        return (
          <div key={index}>
            <button
              onClick={() => {
                handleClick(post);
                setSinglePost(post);
              }}
            >
              {findName(post.userId)}
              {post.body}
            </button>
            <CustomButton post={post} />
          </div>
        );
      })}
    </div>
  );
};

export default DisplayPosts;
