    <div>
      {hidden === false && (
        <div>
          <ErrorBoundary>
            <DisplayCurrentFriends userFriendsList={userFriendsList}
              setHidden={setHidden}
              setSingleUser={setSingleUser}/>
            <DisplayUsers
              userList={userList}
              setHidden={setHidden}
              setSingleUser={setSingleUser}
            />
          </ErrorBoundary>
        </div>
      )}
      {hidden && (
        <DisplaySingleUser
          singleUser={singleUser}
          setHidden={setHidden}
          handleClick={handleClick}
          userId={userId}
          singleUser={singleUser}
          setUserList={setUserList}
          userList={userList}
        />
      )}
    </div>
  );
};

export default FriendsPage;
