import React from "react";
import AxiosPosts from "../../Routes/postRoutes";

const DisplaySinglePosts = ({ post, getThePost, postId }) => {
  useEffect(() => {
    getThePost(postId);
  }, []);

  async function deleteAPost(postId) {
    await AxiosPosts.deletePost(postId);
    return postId;
  }

  return (
    <div>
      {post.body}
      <button onClick={deleteAPost}>Delete Post</button>
    </div>
  );
};

export default DisplaySinglePosts;
