  let updateValue = body.update.value;
  console.log(updateProperty);
  try {
    let response = await axios.put(`${baseUrl}/update`, {
      id: userId,
      body: { [updateProperty]: updateValue },
    });
    console.log(body);
    if (response) {
      return response.data;
    }
  } catch (error) {
    console.log("Error updating user: " + error);
