import "../Posts/MyPost.css";
import AxiosPosts from "../../Routes/postRoutes";
import { useEffect, useState } from "react";

import React from "react";

const DisplayPostsSpot = ({
  spotId,
  update,
  setUpdate,
  singlePost,
  setSinglePost,
  // postList,
  // setPostList,
  hidden,
  setHidden,
  singleSpot,
}) => {
  useEffect(() => {
    getAllPosts(spotId);
    console.log(spotId);
  }, []);

  function handleClick() {
    setUpdate(!update);
  }
  const [postList, setPostList] = useState([]);

  async function getAllPosts() {
    let posts = await AxiosPosts.getPosts();
    if (posts) {
      setPostList(posts);
    } else setPostList([]);
  }

  return (
    <div className="postlist">
      {postList
        .map((post, index) => {
          return (
            <div key={index} className="postbody">
              <button
                className="my-post-button"
                onClick={() => {
                  handleClick(post);
                  setSinglePost(post);
                  setHidden(true);
                }}
              >
                {" "}
                <div className="name-container">{post.name}</div>
                <br />
                <p className="post">Post:</p>
                <div className="body-container">{post.body}</div>
              </button>
            </div>
          );
        })
        .reverse()}
    </div>
  );
};

export default DisplayPostsSpot;
