import "../Posts/MyPost.css";
import AxiosUsers from "../../Routes/userRoutes";
import React from "react";

const DisplayCurrentFriends = ({ userFriendsList, setHidden, setSingleUser }) => {
  function handleClick() {
    setHidden(true);
  }

 async function getFriendById(user){
     let usersFriend = await AxiosUsers.getUser(user)
     if (usersFriend) {return usersFriend.name}
 }
  return (
    <div className="postlist">
      <div>Friends</div>
      {userFriendsList
        .map((user, index) => {
            getFriendById(user)
          return (
            <div key={index} className="postbody">
                {console.log(user)}




              <button
                className="my-post-button"
                onClick={() => {
                  handleClick(user);
                  setSingleUser(user);
                }}
              >
                {" "}
                <div className="name-container">{user}</div>
                <br />
                <p className="post">Post:</p>
                {/* <div className="body-container">{post.body}</div> */}
              </button>


            </div>
          );
        })
        .reverse()}
    </div>
  );
};

export default DisplayCurrentFriends;
