import "../Posts/MyPost.css";

import AxiosSpots from "../../Routes/spotsRoutes";
import React from "react";

const DisplaySingleSpot = ({ singleSpot, setHidden, handleClick, userId }) => {
  async function deleteASpot(spotId) {
    await AxiosSpots.deleteSpot(spotId);
    setHidden(false);
    let click = () => {
      handleClick();
    };
    click();
    return spotId;
  }

  return (
    <div className="delete-post">
      {singleSpot && singleSpot.name} <br />
      {singleSpot.address}
      {console.log(singleSpot.name)}
      <button
        className="my-post-button"
        onClick={() => {

            deleteASpot(singleSpot._id);

        }}
      >
        Delete Spot
      </button>
    </div>
  );
};

export default DisplaySingleSpot;
