import "./NavBar.css";

import { Link, useNavigate } from "react-router-dom";

import AuthContext from "../../context/AuthContext";
import React from "react";
import { useContext } from "react";
import { useState } from "react";

const Navbar = () => {
  const { logoutUser, user } = useContext(AuthContext);
  const [hidden, setHidden] = useState(false);
  const [hamberger, setHamberger] = useState("menu");
  const navigate = useNavigate();
  function handleClick() {
    setHidden(true);
  }
  function switchMenu(){
    if(hamberger === "menu_open"){
setHamberger("menu")}
else setHamberger("menu-open")
  }

  return (
    <div className="navBar">
      <p className="brand">
        <Link
          to="/"
          style={{
            textDecoration: "none",
            color: "white",
            textTransform: "uppercase",
          }}
        >
          <b>
            Skate <span style={{ color: "steelblue" }}>Status</span>
          </b>
        </Link>
      </p>

        <li>{user && <button onClick={() => {
          if (hidden)
                  handleClick();  
                  switchMenu(); 
                }}><span className="material-symbols-outlined">
{hamberger}
</span></button>}</li>
{hidden && (

  <ul>
         <li>{user && <button onClick={() => navigate("/")}>Home</button>}</li>
         <li>{user && <button onClick={() => navigate("/spots")}>Spots</button>}</li>
         <li>
           {user && (
             <button onClick={() => navigate("/myPosts")}>My Posts</button>
           )}
         </li>
         <li>
           {user && (
             <button onClick={() => navigate("/feedPage")}>Friends Feed</button>
           )}
         </li>
         <li>
           {user && (
             <button onClick={() => navigate("/profile")}>Profile</button>
           )}
         </li>
         <li>
           {user && (
             <button onClick={() => navigate("/friendsPage")}>Friends</button>
           )}
         </li>
         <li>
           {user ? (
             <button onClick={logoutUser}>Logout</button>
           ) : (
             <button onClick={() => navigate("/login")}>Login</button>
           )}
         </li>
         </ul>
      )}


    </div>
  );
};

export default Navbar;
