import React from "react";

const DisplayPosts = ({ postList, deleteAPost }) => {
  return (
    <div>
      {postList.map((post) => {
        return (
          <div>
            {post.body}
            <button onClick={(event) => deleteAPost(post._id)}>X</button>
            onChange={(event) => {
          setSearchText(event.target.value);
        }}
          </div>
        );
      })}
    </div>
  );
};

export default DisplayPosts;
