import React, { useContext, useEffect, useState } from "react";
import CreatePost from "../../components/Posts/createPosts";
import DisplayPosts from "../../components/Posts/displayPosts";
import AxiosPosts from "../../Routes/postRoutes";
import AuthContext from "../../context/AuthContext";
import ErrorBoundary from "../ErrorBoundary";
import DisplaySinglePosts from "../../components/Posts/displaySinglePost";

const MyPosts = () => {
  const [postList, setPostList] = useState([]);
  const [post, setPost] = useState("");
  const { user } = useContext(AuthContext);
  const userId = user._id || null;
  const [update, setUpdate] = useState(false);
  const [hidden, setHidden] = useState(false);
  const [singlePost, setSinglePost]= 

  useEffect(() => {
    getPosts(userId);
  }, [update]);

  function handleClick() {
    setUpdate(!update);
  }

  async function getPosts(userId) {
    let posts = await AxiosPosts.getPosts(userId);
    if (Object.keys(posts).length !== 0) {
      setPostList(posts);
    } else setPostList({ Object: "No Posts" });
  }

  return (
    <div>
      {hidden === false && (
        <div>
          <CreatePost
            post={post}
            setPost={setPost}
            setPostList={setPostList}
            postList={postList}
            userId={userId}
            handleClick={handleClick}
          />
          <ErrorBoundary>
            <DisplayPosts
              postList={postList}
              post={post}
              hidden={hidden}
              setHidden={setHidden}
            />
          </ErrorBoundary>
        </div>
      )}
      {hidden && <DisplaySinglePosts post={post} />}
    </div>
  );
};

export default MyPosts;
