import "../Posts/MyPost.css";
import AxiosPostsa from "../../Routes/postRoutes";


import React from "react";

const DisplayPostsSpot = ({ postList, setHidden, setSinglePost }) => {

  async function getPosts(postId) {
    let posts = await AxiosPosts.getAPostBySpotId(spotId);
    if (posts) {
      setPostList(posts);
    } else setPostList({ Object: "No Posts" });
  }
  function handleClick() {
    setHidden(true);
  }
  getPosts(spotId);

  return (
    <div className="postlist">
      {postList
        .map((post, index) => {
          return (
            <div key={index} className="postbody">
              <button
                className="my-post-button"
                onClick={() => {
                  handleClick(post);
                  setSinglePost(post);
                }}
              >
                {" "}
                <div className="name-container">{post.name}</div>
                <br />
                <p className="post">Post:</p>
                <div className="body-container">{post.body}</div>
              </button>


            </div>
          );
        })
        .reverse()}
    </div>
  );
};

export default DisplayPostsSpot;
