import AxiosPosts from "../../Routes/postRoutes";

const SinglePost = () => {
  async function getThePost(postId) {
    let post = await AxiosPosts.getAPost(postId);

    return (
      <div>
        <DisplaySinglePost post={post} getThePost={getThePost} postId={postId} />
      </div>
    );
  }
};

export default SinglePost;
